"use strict";
{
    // Basic Data Types
    const myName = "Abdullah"; // implicit data type. ts infer the type based on value
    const secondName = "Al Mubin"; // Explicit data type
    // Primitive data types
    const fullName = "Abdullah";
    const age = 25;
    const isMarried = false;
    const spouse = undefined;
    const bankBalance = null;
    // Non Primitive data types
    // Array
    const friends = ["Rakib", "Akib", "Sakib"];
    // Object
    const family = {
        thana: "Hathazari",
        village: "Chipatoli",
        father: "Abdul Mannan",
        sibling: 2,
    };
    // tuple | tuple is an array, it maintains type of values
    const coordinates = [7, 19];
    const x = ["Abdullah", 25];
    const y = ["Abdullah", 25, true];
}
